script:
frappe.ui.form.on('Vertrag', {
    customer: function(frm) {
        if (frm.doc.customer) {
            frappe.db.get_value('Customer', frm.doc.customer, 'customer_primary_address')
                .then(response => {
                    let address_name = response.message.customer_primary_address;
                    if (address_name) {
                        frm.set_value('customer_address', address_name);
                    } else {
                        frm.set_value('customer_address', null);
                    }
                });
        }
    }
});

